package programmers;

import java.util.*;

public class PG_12904 {
    public static void main(String[] args) {
        String s = "";
        int result = solution(s);
        System.out.println(result);
    }
    public static int solution(String s){
        int answer = 1;
        char[] orderArr = s.toCharArray();
        int len = orderArr.length;
        for(int start=0;start<len;start++){
            for(int end=start+1;end<len;end++){
                int limitCompare = (end-start)/2+1;
                boolean same = true;
                for(int compare=0;compare<limitCompare;compare++){
                    if(orderArr[compare+start] != orderArr[end-compare]){
                        same = false;
                        break;
                    }
                }
                if(same) answer = Math.max(answer, end-start+1);
            }
        }

        return answer;
    }
}
